import React, {Component} from 'react';

export interface ExpandableOptionProps {
    outline: string,
    description: string,
    readMore?: string,
}

interface State {
    open: boolean
}

class ExpandableOption extends Component<ExpandableOptionProps, State> {
    state: State = {
        open: false,
    };

    toggleOpen() {
        this.setState({ open: !this.state.open });
    }

    render() {
        const {outline, description} = this.props;
        const {open} = this.state;

        return (
            <div>
                <h5 onClick={this.toggleOpen}>{outline}</h5>
                {open && description}
            </div>
        );
    }
}


export default ExpandableOption
