import React, {Component} from 'react';
import Modal from 'react-modal';
import './css/expandable-option.less';
import classNames from 'classnames';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'

export interface ExpandableOptionProps {
    outline: string,
    description: string,
}

interface State {
    open: boolean,
    mousedover: boolean,
}

const customStyles = {
    content : {
      top                   : '50%',
      left                  : '50%',
      right                 : 'auto',
      bottom                : 'auto',
      marginRight           : '-50%',
      transform             : 'translate(-50%, -50%)'
    }
};

class ExpandableOption extends Component<ExpandableOptionProps, State> {

    state: State = {
        open: false,
        mousedover: false
    };

    toggleOpen = () => {
        this.setState({ open: !this.state.open });
    }

    openModal = () => {
        this.setState({ readMoreShown: true})
    }

    closeModal = () => {
        this.setState({readMoreShown: false});
    }

    onMouseEnter = () => {
        this.setState({mousedover: true});
    }

    onMouseLeave = () => {
        this.setState({mousedover: false});
    }

    render() {
        const {outline, description} = this.props;
        const {open, readMoreShown, mousedover} = this.state;

        return (
            <div className='expandable-option'>
                <FontAwesomeIcon icon={open ? 'angle-down' : 'angle-right'}/>
                <div 
                    onClick={this.toggleOpen} 
                    onMouseEnter={this.onMouseEnter} 
                    onMouseLeave={this.onMouseLeave}
                    className={classNames('expandable-option-outline', {'expandable-option-mousedover': mousedover})}>
                    {outline}
                </div>
                {open 
                    && <div className={classNames('expandable-option-description')}>{description}</div>}
                {/* {this.props.readMore && readMoreShown && <p onClick={this.openModal}>Read More</p>}
                {readMoreShown && this.props.readMore &&
                    <Modal 
                        isOpen={readMoreShown} 
                        // onAfterOpen={this.afterOpenModal}
                        onRequestClose={this.closeModal}
                        style={customStyles}
                        contentLabel="Example Modal"
                    >
                        hello
                    </Modal>
                } */}
            </div>
        );
    }
}


export default ExpandableOption
