import React, {Component} from 'react';
import InfoSection from './InfoSection';
import {ExpandableOptionProps} from './ExpandableOption';
import './css/step.less';
import classNames from 'classnames';
import { tryStatement } from '@babel/types';

export interface StepProps {
    num: string,
    title: string,
    subtitle: string,
    overviewText: string,
    overviewOptions: ExpandableOptionProps[],
    recommendationsText: string,
    recommendationsOptions: ExpandableOptionProps[],
    videoUrl?: string,
};

class Step extends Component<StepProps> {

    render() {
        const {
            num, 
            title, 
            subtitle, 
            overviewText, 
            overviewOptions, 
            recommendationsText, 
            recommendationsOptions,
            videoUrl } = this.props;

        return (
            <div className='step'>
                <div className={classNames('step-header')}>
                    <div className={classNames('step-header-info', {'step-header-info-short': videoUrl})}>
                        <h1 className='step-text-title'><span className='step-text-title-roman'>{num}.</span> {title}</h1>
                        <div className={classNames('step-header-info-subtitle-area-wrapper')}> 
                            <div className='step-header-info-subtitle-area' dangerouslySetInnerHTML={{__html: subtitle}}></div>
                        </div>
                    </div>
                    {videoUrl && <div className='step-header-video-area-wrapper'>
                        <div className='step-header-video-area'>
                            <iframe 
                                frameBorder='0'
                                width="560" height="315"
                                className='step-header-video-area-frame' 
                                allow="accelerometer; autoplay; 
                                encrypted-media; gyroscope; picture-in-picture"
                                src={videoUrl}>
                            </iframe>
                        </div>
                    </div>}

                </div>
                <div className='step-info-area'>
                    <InfoSection type='Overview' text={overviewText} options={overviewOptions}/>
                    <InfoSection type='Recommendations' text={recommendationsText} options={recommendationsOptions}/>
                </div>
            </div>
        );
    };
};

export default Step;
