import React, {Component} from 'react';
import Modal from 'react-modal';
import './css/expandable-option.less';
import classNames from 'classnames';

export interface ExpandableOptionProps {
    outline: string,
    description: string,
    readMore?: boolean,
}

interface State {
    open: boolean
    readMoreShown: boolean
}

const customStyles = {
    content : {
      top                   : '50%',
      left                  : '50%',
      right                 : 'auto',
      bottom                : 'auto',
      marginRight           : '-50%',
      transform             : 'translate(-50%, -50%)'
    }
};

class ExpandableOption extends Component<ExpandableOptionProps, State> {
    static defaultProps = {
        readMore: false
    }

    state: State = {
        open: false,
        readMoreShown: false
    };

    toggleOpen = () => {
        this.setState({ open: !this.state.open });
    }

    openModal = () => {
        this.setState({ readMoreShown: true})
    }

    closeModal = () => {
        this.setState({readMoreShown: false});
    }

    render() {
        const {outline, description} = this.props;
        const {open, readMoreShown} = this.state;

        return (
            <div>
                <div onClick={this.toggleOpen}>{outline}</div>
                {open && description}
                {this.props.readMore && readMoreShown && <p onClick={this.openModal}>Read More</p>}
                {readMoreShown && this.props.readMore &&
                    <Modal 
                        isOpen={readMoreShown} 
                        // onAfterOpen={this.afterOpenModal}
                        onRequestClose={this.closeModal}
                        style={customStyles}
                        contentLabel="Example Modal"
                    >
                        hello
                    </Modal>
                }
            </div>
        );
    }
}


export default ExpandableOption
