import React, {Component} from 'react';
import Step, {StepProps} from './Step';
import converter from 'number-to-words';
import './css/body.less';
import classNames from 'classnames';
import info from './steps.json';


const steps: StepProps[] = info;

class Body extends React.Component {

    private myRef = React.createRef<HTMLDivElement>();

    // constructor() {
    //     super({});
    //     this.myRef = React.createRef();
    // }

    render() {
        return (
            <div className='body'>
                <div className={classNames('body-landing')}>
                    <div className='body-landing-main'>
                        <div className='body-landing-main-heading'>Evicted in Providence</div> 
                        <div className='body-landing-main-subheading'>
                            a roadmap to reforming the Rhode Island eviction system
                        </div>
                        <div className='body-landing-main-instructions'>
                            <div>If you are facing in eviction, find our emergency tenant resources <a href='https://docs.google.com/document/d/1viyhXL5q8cPCpN-JnkEs8Cbgi4qSZArjriizxT-2Cv4/edit?usp=sharing' target='_blank'>here</a>.</div>
                            <div>If you are a legislator, find our policy quicksheet here. </div>
                            <div>If you are a journalist, feel free to quote individual interviews, with the proper credit. </div>
                            <div>If you are a landlord, this site is for you, too.</div>
                        </div>
                    </div>

                    <div ref={this.myRef} className='body-landing-about'>
                        <div className='body-landing-about-one'>Across the nation, everyone - from housing advocates to luxury developers - understands that our 
                        system for producing and distributing housing is broken.</div>
                        <div className='body-landing-about-two'>Despite this consensus, 
                        obsolete eviction procedures punish the most vulnerable for market failures beyond their control.</div>
                        <div className='body-landing-about-three'>Signs of Providence has spent the past year interviewing people facing eviction, lawyers, social workers, housing advocates, elected officials, and landlords to create a roadmap to eviction reform in Rhode Island. </div>
                        <div className='body-landing-about-four'>This project is heavily indebted to our interviewees, as well as research conducted by Professor Eric Hirsch, HousingWorks RI, and the Eviction Lab. </div>
                        <div className='body-landing-about-five'>Signs of Providence is a Rhode Island-based collective creating media to confront the ongoing housing crisis. Learn more about us <a href='http://signsofprovidence.org' target='_blank'>here</a>. </div>
                    </div> 
                </div>

                <div className={classNames('body-steps')}>
                    {steps.map((s, i)  => {
                        const key = converter.toWords(i + 1);
                        return <Step key={ key } num = { key } { ...s }/>
                    })}
                </div>

            </div>
        );
    }
};

export default Body;
